package ecl

import (
	"fmt"
	"testing"

	"github.com/hashicorp/terraform/helper/resource"
	"github.com/hashicorp/terraform/terraform"
)

func TestAccBaremetalV2KeypairDataSourceBasic(t *testing.T) {
	resource.Test(t, resource.TestCase{
		PreCheck:  func() { testAccPreCheck(t) },
		Providers: testAccProviders,
		Steps: []resource.TestStep{
			resource.TestStep{
				Config: testAccBaremetalV2KeypairDataSourceBasic,
				Check: resource.ComposeTestCheckFunc(
					testAccCheckBaremetalV2KeypairDataSourceID("data.ecl_baremetal_keypair_v2.keypair_1"),
					resource.TestCheckResourceAttr(
						"data.ecl_baremetal_keypair_v2.keypair_1", "name", "the-key-name"),
					resource.TestCheckResourceAttr(
						"data.ecl_baremetal_keypair_v2.keypair_1", "public_key", "ssh-rsa  jrp-hp-pc"),
				),
			},
		},
	})
}

func testAccCheckBaremetalV2KeypairDataSourceID(n string) resource.TestCheckFunc {
	return func(s *terraform.State) error {
		rs, ok := s.RootModule().Resources[n]
		if !ok {
			return fmt.Errorf("Can't find keypair data source: %s", n)
		}

		if rs.Primary.ID == "" {
			return fmt.Errorf("Keypair data source ID not set")
		}

		return nil
	}
}

const testAccBaremetalV2KeypairDataSourceBasic = `
resource "ecl_baremetal_keypair_v2" "keypair_1" {
  name = "the-key-name"
  public_key = "ssh-rsa  jrp-hp-pc"
}

data "ecl_baremetal_keypair_v2" "keypair_1" {
  name = "${ecl_baremetal_keypair_v2.keypair_1.name}"
}
`
