package ecl

import (
	"fmt"
	"testing"

	"github.com/hashicorp/terraform/helper/resource"
	"github.com/hashicorp/terraform/terraform"
)

func TestAccComputeV2KeypairDataSource_basic(t *testing.T) {
	resource.Test(t, resource.TestCase{
		PreCheck:     func() { testAccPreCheck(t) },
		Providers:    testAccProviders,
		CheckDestroy: testAccCheckComputeV2KeypairDestroy,
		Steps: []resource.TestStep{
			resource.TestStep{
				Config: testAccComputeV2KeypairDataSourceBasic,
				Check: resource.ComposeTestCheckFunc(
					testAccCheckComputeV2KeypairDataSourceID("data.ecl_compute_keypair_v2.kp"),
					resource.TestCheckResourceAttr(
						"data.ecl_compute_keypair_v2.kp", "name", "the-key-name"),
					resource.TestCheckResourceAttr(
						"data.ecl_compute_keypair_v2.kp", "public_key", "ssh-rsa  jrp-hp-pc"),
				),
			},
		},
	})
}

func testAccCheckComputeV2KeypairDataSourceID(n string) resource.TestCheckFunc {
	return func(s *terraform.State) error {
		rs, ok := s.RootModule().Resources[n]
		if !ok {
			return fmt.Errorf("Can't find keypair data source: %s", n)
		}

		if rs.Primary.ID == "" {
			return fmt.Errorf("Keypair data source ID not set")
		}

		return nil
	}
}

const testAccComputeV2KeypairDataSourceBasic = `
resource "ecl_compute_keypair_v2" "kp" {
  name = "the-key-name"
  public_key = "ssh-rsa  jrp-hp-pc"
}

data "ecl_compute_keypair_v2" "kp" {
  name = "${ecl_compute_keypair_v2.kp.name}"
}
`
